import React, {useEffect, useState} from "react";
import { Link, useParams, useRouteMatch } from "react-router-dom";
import { readDeck } from "../utils/api/index"

function DeckEdit() {
    const [deck, setDeck] = useState({deckId: 1});

    const {url, path} = useRouteMatch();
    const {deckId} = useParams()
    // console.log('url', url)
    // console.log('path', path)
    // console.log('params', deckId)
    useEffect(() => {
        async function loadDeck() {
          const response = await readDeck(deckId);
          setDeck(response);
        }
        loadDeck();
      }, [deckId]);
      console.log(deck)
    return (
        <div>
      <nav class="navbar navbar-light bg-light">
        <div class="collapse navbar-collapse d-flex">
          <ul class="navbar">
            <a class="navbar-item" href="/">
              Home
            </a>
            <Link to={`/decks/${deckId}`}>/ {deck.name} </Link>
            <a> / Edit Deck</a>
          </ul>
        </div>
      </nav>
      <div>
          <h1>Edit Deck</h1>
          <h6>Name</h6>
          <textarea placeholder={`${deck.name}`}></textarea>
          <h6>Description</h6>
          <textarea placeholder={`${deck.description}`}></textarea>
      </div>
      <div>
          <Link to={`/decks/${deckId}`} class="btn btn-secondary">Cancel</Link>
          <button class="btn btn-primary">Submit</button>
      </div>
    </div>
    )
}

export default DeckEdit
