import React, { useEffect, useState } from "react";
import { Link, useRouteMatch, useParams } from "react-router-dom";
import { readDeck } from "../utils/api/index";

function Deck() {
  const [deck, setDeck] = useState({ cards: [] });
  const [cards, setCards] = useState([]);
  const { deckId } = useParams();
  const { url, path } = useRouteMatch();
  console.log("url", url);
  console.log("path", path);
  useEffect(() => {
    async function loadDeck() {
      const response = await readDeck(deckId);
      setDeck(response);
    }
    loadDeck();
  }, [deckId]);
  useEffect(() => {
    const deckCards = deck.cards;
    if (deck.cards.length > 0) {
      let card = [];
      for (let i = 0; i < deckCards.length; i++) {
        card.push({
          front: deckCards[i].front,
          back: deckCards[i].back,
          id: deckCards[i].id,
          deckId: deckCards[i].deckId,
        });
      }
      setCards(card);
    }
  }, [deck]);
  const cardList = cards.map
  return (
    <div>
      <nav class="navbar navbar-light bg-light">
        <div class="collapse navbar-collapse d-flex">
          <ul class="navbar">
            <a class="navbar-brand" href="/">
              Home
            </a>
            <a>/ {deck.name} </a>
          </ul>
        </div>
      </nav>
      <div>
        <h3>{deck.name}</h3>
        <p>{deck.description}</p>
        <Link class="btn btn-secondary" to={`${url}/edit`}>
          Edit
        </Link>
        <Link class="btn btn-primary" to={`${url}/study`}>
          Study
        </Link>
        <Link class="btn btn-primary" to={`${url}/cards/new`}>
          Add Cards
        </Link>
      </div>
      <div>

      </div>
    </div>
  );
}

export default Deck;
