import { React, useEffect, useState } from "react";
import Header from "./Header";
import NotFound from "./NotFound";
import {createCard, listDecks, createDeck} from "../utils/api/index.js"
import { Route, Switch, Link, BrowserRouter as Router } from "react-router-dom";
import Home from "./Home";
import DeckView from "./DeckView";
import AddCard from "./AddCard";
import Deck from "./Deck";
import DeckEdit from "./DeckEdit";
import CardEdit from "./CardEdit";
// import Card from "./Card";


function Layout() {
  const [decks, setDecks] = useState({name: 'loading..'});
  const [deckDesc, setDeckDesc] = useState([]);

  useEffect(() => {
    async function loadDeck() {
      const response = await listDecks();
      setDecks(response)
    }
    loadDeck();
  }, [])

    useEffect(() => {
      if (decks.length > 0) {
        let desc = [];
        for (let i = 0; i < decks.length; i++){
          desc.push({name: decks[i].name, id: decks[i].id, description: decks[i].description, length: decks[i].cards.length})
        }
        // const desc = decks.map(({name, id, description, cards}) => ({name, id, description}))
        setDeckDesc(desc)
      }
    }, [decks])

    function handleDelete(event) {
        // if(window.confirm('Delete this deck? You will not be albe to recover it.')){
        //   window.location.reload()
        // }
        // else {

        // }
    } 
  return (
    <div>
      <Header />

        {/* TODO: Implement the screen starting here */}
        <Router>
        <Switch>
          <Route exact path="/">
            <Link to="/decks/new" class="btn btn-secondary">Create Deck</Link>
            <Home decks={decks} deckDesc={deckDesc} handleDelete={handleDelete} />
          </Route>
          <Route path="/decks/:deckId/study">
            <DeckView />
          </Route>
          <Route path="/decks/:deckId/cards/new">
            <AddCard />
          </Route>
          <Route path="/decks/:deckId/edit">
            <DeckEdit />
          </Route>
          <Route path="/decks/:deckId/cards/:cardId/edit">
            <CardEdit />
          </Route>
          <Route path="/decks/:deckId">
            <Deck handleDelete={handleDelete} />
          </Route>
          <Route>
           <NotFound />
          </Route>
        </Switch>
        </Router>
    </div>
  );
}

export default Layout;
