import React, { useState, useEffect } from "react";
import DeckList from "./DeckList";
import { listDecks } from "../utils/api/index.js";

function Home() {
  const [decks, setDecks] = useState([{ name: "loading.." }]);
  const [deckDesc, setDeckDesc] = useState([]);

  useEffect(() => {
    async function loadDeck() {
      console.log(await listDecks());
      const response = await listDecks();
      setDecks(response);
    }
    loadDeck();
  }, []);

  const deck = decks.map((deck) => <DeckList deck={deck} key={deck.id} />);
  console.log(deck);
  return <div>{deck}</div>;
}

export default Home;
