import React, { useEffect, useState } from "react";
import { Link, useRouteMatch, useParams, useHistory } from "react-router-dom";
import { readDeck, createCard } from "../utils/api/index";

function AddCard() {
    const history = useHistory();
  const [deck, setDeck] = useState({ name: "" });
  const [front, setFront] = useState()
  const [back, setBack] = useState()
  const { deckId } = useParams();
    const [max, setMax] = useState(0);
  useEffect(() => {
    async function loadDeck() {
      const response = await readDeck(deckId);
      setDeck(response);
      const cards = response.cards
      cards.forEach(card => {
          if(card.id > max) {
              setMax(card.id)
          }
      })
    // for (let i = 0; i < cards.length; i++){
    //     if (cards[i].id > max){
    //         console.log(cards[i].id)
    //         max = cards[i].id
    //     }
    // }
    // max = cards.reduce(
    //     (max, card) => (card.id > max ? card.id : max),
    //     cards[0].id
    // )
    // max = Math.max(...cards.map(card => card.id))
    }
    loadDeck();
  }, []);
  const handleFrontChange = (event) => setFront(event.target.value)
  const handleBackChange = (event) => setBack(event.target.value)
  const handleSave = () => {
      let cardId = max + 1
      const card = {"front": front, "back": back }
      console.log(card)
      createCard(deckId, card)
      .then((response) => console.log(response))
    //   console.log(updateDeck(signal))
    //   const result = window.confirm("Save?")
  }
  return (
    <div>
      <nav class="navbar navbar-light bg-light">
        <div class="collapse navbar-collapse d-flex">
          <ul class="navbar">
            <a class="navbar-brand" href="/">
              Home
            </a>
            <a>/ {deck.name} </a>
            <a> / Add Card</a>
          </ul>
        </div>
      </nav>
      <div>
        <h4>Front</h4>
        <textarea placeholder="Front side of card" onChange={handleFrontChange}></textarea>
        <h4>Back</h4>
        <textarea placeholder="Back side of card" onChange={handleBackChange}></textarea>
        <div>
          <button class="btn btn-secondary" onClick={()=> history.push("/")}>Done</button>
          <button class="btn btn-primary" onClick={handleSave}>Save</button>
        </div>
      </div>
    </div>
  );
}
export default AddCard;
